                  <p className="text-gray-600 dark:text-gray-400 mb-6">Retournez Ã  l'Ã©tape prÃ©cÃ©dente pour activer cette option</p>
                  <button
                    onClick={() => setCurrentStep(1)}
                    className="px-6 py-3 bg-amber-600 text-white font-bold rounded-xl"
                  >
                    Retour aux Options
                  </button>
                </div>
              )}

              {/* Step 3: Passenger Info */}
              {currentStep === 3 && (
                <PassengerForm 
                  passengers={passengers}
                  onPassengersUpdate={setPassengerData}
                />
              )}

              {/* Step 4: Summary */}
              {currentStep === 4 && (
                <div className="bg-white dark:bg-gray-800 rounded-3xl p-6 shadow-xl">
                  <h2 className="text-2xl font-black mb-6">RÃ©capitulatif de Votre RÃ©servation</h2>

                  <div className="space-y-6">
                    {/* Flight Summary */}
                    <div className="p-4 bg-gray-50 dark:bg-gray-900 rounded-xl">
                      <h3 className="font-bold mb-3">Vol</h3>
                      <p className="text-sm text-gray-600 dark:text-gray-400">
                        {flight.departure_airport.city} ({flight.departure_airport.code}) â†’ {flight.arrival_airport.city} ({flight.arrival_airport.code})
                      </p>
                      <p className="text-sm text-gray-600 dark:text-gray-400">
                        {new Date(flight.departure_date).toLocaleDateString('fr-FR')} â€¢ {flight.departure_time} - {flight.arrival_time}
                      </p>
                    </div>

                    {/* Passengers Summary */}
                    <div className="p-4 bg-gray-50 dark:bg-gray-900 rounded-xl">
                      <h3 className="font-bold mb-3">Passagers ({passengers})</h3>
                      {passengerData.map((p, i) => (
                        <div key={i} className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                          {i + 1}. {p.title} {p.firstName} {p.lastName}
                          {selectedSeats[i] && ` - SiÃ¨ge ${selectedSeats[i]}`}
