import React, { useState } from 'react';
import { useLanguage } from '../../contexts/LanguageContext';
import { useCurrency } from '../../contexts/CurrencyContext';
import amadeusService from '../../services/amadeusService';

const FlightSearch = ({ onSearchResults, onLoading }) => {
  const { t } = useLanguage();
  const { currency } = useCurrency();

  const [searchParams, setSearchParams] = useState({
    origin: '',
    destination: '',
    departureDate: '',
    returnDate: '',
    adults: 1,
    children: 0,
    infants: 0,
    travelClass: 'ECONOMY',
    nonStop: false
  });

  const [originSuggestions, setOriginSuggestions] = useState([]);
  const [destinationSuggestions, setDestinationSuggestions] = useState([]);
  const [showOriginSuggestions, setShowOriginSuggestions] = useState(false);
  const [showDestinationSuggestions, setShowDestinationSuggestions] = useState(false);
  const [isSearching, setIsSearching] = useState(false);
  const [error, setError] = useState(null);

  // Recherche d'aéroports
  const searchAirports = async (keyword, type) => {
    if (keyword.length < 2) {
      if (type === 'origin') setOriginSuggestions([]);
      else setDestinationSuggestions([]);
      return;
    }

    try {
      const response = await amadeusService.searchAirports(keyword);
      const airports = response.data?.data || [];

      if (type === 'origin') {
        setOriginSuggestions(airports);
        setShowOriginSuggestions(true);
      } else {
        setDestinationSuggestions(airports);
        setShowDestinationSuggestions(true);
      }
    } catch (error) {
      console.error('Error searching airports:', error);
    }
  };

  // Sélectionner un aéroport
  const selectAirport = (airport, type) => {
    const iataCode = airport.iataCode;
    if (type === 'origin') {
      setSearchParams({ ...searchParams, origin: iataCode });
      setShowOriginSuggestions(false);
    } else {
      setSearchParams({ ...searchParams, destination: iataCode });
      setShowDestinationSuggestions(false);
    }
  };

  // Gérer la recherche
  const handleSearch = async (e) => {
    e.preventDefault();
    setError(null);
    setIsSearching(true);

    if (onLoading) onLoading(true);

    try {
      const results = await amadeusService.searchFlights({
        ...searchParams,
        currencyCode: currency.code
      });

      if (onSearchResults) {
        onSearchResults(results);
      }
    } catch (error) {
      console.error('Search error:', error);
      setError(error.response?.data?.message || 'Erreur lors de la recherche de vols');
    } finally {
      setIsSearching(false);
      if (onLoading) onLoading(false);
    }
  };

  return (
    <div className="bg-white rounded-2xl shadow-2xl p-8">
      <h2 className="text-3xl font-bold text-gray-800 mb-6">
        {t('searchFlights', 'Rechercher un vol')}
      </h2>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <form onSubmit={handleSearch}>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          {/* Origine */}
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {t('from', 'Départ')} *
            </label>
            <div className="relative">
              <input
                type="text"
                value={searchParams.origin}
                onChange={(e) => {
                  setSearchParams({ ...searchParams, origin: e.target.value });
                  searchAirports(e.target.value, 'origin');
                }}
                onFocus={() => setShowOriginSuggestions(true)}
                placeholder="ABJ - Abidjan"
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                required
              />
              <i className="fas fa-plane-departure absolute right-4 top-4 text-gray-400"></i>
            </div>

            {/* Suggestions origine */}
            {showOriginSuggestions && originSuggestions.length > 0 && (
              <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-lg shadow-lg max-h-60 overflow-y-auto">
                {originSuggestions.map((airport) => (
                  <div
                    key={airport.id}
                    onClick={() => selectAirport(airport, 'origin')}
                    className="px-4 py-3 hover:bg-purple-50 cursor-pointer border-b border-gray-100 last:border-b-0"
                  >
                    <div className="font-semibold text-gray-800">
                      {airport.iataCode} - {airport.name}
                    </div>
                    <div className="text-sm text-gray-600">
                      {airport.address?.cityName}, {airport.address?.countryName}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Destination */}
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {t('to', 'Destination')} *
            </label>
            <div className="relative">
              <input
                type="text"
                value={searchParams.destination}
                onChange={(e) => {
                  setSearchParams({ ...searchParams, destination: e.target.value });
                  searchAirports(e.target.value, 'destination');
                }}
                onFocus={() => setShowDestinationSuggestions(true)}
                placeholder="CDG - Paris"
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                required
              />
              <i className="fas fa-plane-arrival absolute right-4 top-4 text-gray-400"></i>
            </div>

            {/* Suggestions destination */}
            {showDestinationSuggestions && destinationSuggestions.length > 0 && (
              <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-lg shadow-lg max-h-60 overflow-y-auto">
                {destinationSuggestions.map((airport) => (
                  <div
                    key={airport.id}
                    onClick={() => selectAirport(airport, 'destination')}
                    className="px-4 py-3 hover:bg-purple-50 cursor-pointer border-b border-gray-100 last:border-b-0"
                  >
                    <div className="font-semibold text-gray-800">
                      {airport.iataCode} - {airport.name}
                    </div>
                    <div className="text-sm text-gray-600">
                      {airport.address?.cityName}, {airport.address?.countryName}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Date de départ */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {t('departureDate', 'Date de départ')} *
            </label>
            <input
              type="date"
              value={searchParams.departureDate}
              onChange={(e) => setSearchParams({ ...searchParams, departureDate: e.target.value })}
              min={new Date().toISOString().split('T')[0]}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
              required
            />
          </div>

          {/* Date de retour */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {t('returnDate', 'Date de retour')} ({t('optional', 'optionnel')})
            </label>
            <input
              type="date"
              value={searchParams.returnDate}
              onChange={(e) => setSearchParams({ ...searchParams, returnDate: e.target.value })}
              min={searchParams.departureDate || new Date().toISOString().split('T')[0]}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            />
          </div>
        </div>

        {/* Passagers et classe */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {t('adults', 'Adultes')}
            </label>
            <select
              value={searchParams.adults}
              onChange={(e) => setSearchParams({ ...searchParams, adults: parseInt(e.target.value) })}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            >
              {[1, 2, 3, 4, 5, 6, 7, 8, 9].map(num => (
                <option key={num} value={num}>{num}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {t('children', 'Enfants')}
            </label>
            <select
              value={searchParams.children}
              onChange={(e) => setSearchParams({ ...searchParams, children: parseInt(e.target.value) })}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            >
              {[0, 1, 2, 3, 4, 5, 6, 7, 8].map(num => (
                <option key={num} value={num}>{num}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {t('infants', 'Bébés')}
            </label>
            <select
              value={searchParams.infants}
              onChange={(e) => setSearchParams({ ...searchParams, infants: parseInt(e.target.value) })}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            >
              {[0, 1, 2, 3, 4].map(num => (
                <option key={num} value={num}>{num}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {t('class', 'Classe')}
            </label>
            <select
              value={searchParams.travelClass}
              onChange={(e) => setSearchParams({ ...searchParams, travelClass: e.target.value })}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            >
              <option value="ECONOMY">{t('economy', 'Économique')}</option>
              <option value="PREMIUM_ECONOMY">{t('premiumEconomy', 'Éco Premium')}</option>
              <option value="BUSINESS">{t('business', 'Affaires')}</option>
              <option value="FIRST">{t('first', 'Première')}</option>
            </select>
          </div>
        </div>

        {/* Options */}
        <div className="mb-6">
          <label className="flex items-center">
            <input
              type="checkbox"
              checked={searchParams.nonStop}
              onChange={(e) => setSearchParams({ ...searchParams, nonStop: e.target.checked })}
              className="w-5 h-5 text-purple-600 border-gray-300 rounded focus:ring-purple-500"
            />
            <span className="ml-2 text-gray-700">
              {t('directFlightsOnly', 'Vols directs uniquement')}
            </span>
          </label>
        </div>

        {/* Bouton de recherche */}
        <button
          type="submit"
          disabled={isSearching}
          className="w-full bg-gradient-to-r from-purple-600 to-purple-800 hover:from-purple-700 hover:to-purple-900 text-white font-bold py-4 px-8 rounded-lg transition-all duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-3"
        >
          {isSearching ? (
            <>
              <i className="fas fa-spinner fa-spin"></i>
              {t('searching', 'Recherche en cours...')}
            </>
          ) : (
            <>
              <i className="fas fa-search"></i>
              {t('searchFlights', 'Rechercher des vols')}
            </>
          )}
        </button>
      </form>
    </div>
  );
};

export default FlightSearch;
