import React, { useState } from 'react';
import { flightService } from '../../services/api';
import { useLanguage } from '../../contexts/LanguageContext';
import { useCurrency } from '../../contexts/CurrencyContext';

const FlightSearch = ({ onSearchResults, onLoading }) => {
  const { t } = useLanguage();
  const { currency } = useCurrency();

  const [searchParams, setSearchParams] = useState({
    origin: '',
    destination: '',
    departureDate: '',
    returnDate: '',
    adults: 1,
    children: 0,
    infants: 0,
    travelClass: 'ECONOMY',
    nonStop: false
  });

  const [originSuggestions, setOriginSuggestions] = useState([]);
  const [destinationSuggestions, setDestinationSuggestions] = useState([]);
  const [showOriginSuggestions, setShowOriginSuggestions] = useState(false);
  const [showDestinationSuggestions, setShowDestinationSuggestions] = useState(false);
  const [isSearching, setIsSearching] = useState(false);
  const [error, setError] = useState(null);

  const [selectedOrigin, setSelectedOrigin] = useState(null);
  const [selectedDestination, setSelectedDestination] = useState(null);
  const [tripType, setTripType] = useState('roundtrip');

  // Recherche d'aéroports
  const searchAirports = async (keyword, type) => {
    if (keyword.length < 3) {
      if (type === 'origin') setOriginSuggestions([]);
      else setDestinationSuggestions([]);
      return;
    }

    try {
      const results = await flightService.getAirports(keyword);
      if (type === 'origin') {
        setOriginSuggestions(results || []);
        setShowOriginSuggestions(true);
      } else {
        setDestinationSuggestions(results || []);
        setShowDestinationSuggestions(true);
      }
    } catch (err) {
      console.error('Erreur recherche aéroports:', err);
      if (type === 'origin') setOriginSuggestions([]);
      else setDestinationSuggestions([]);
    }
  };

  const selectAirport = (airport, type) => {
    const displayText = `${airport.name} (${airport.iataCode})`;
    if (type === 'origin') {
      setSelectedOrigin(airport);
      setSearchParams({ ...searchParams, origin: airport.iataCode });
      setShowOriginSuggestions(false);
    } else {
      setSelectedDestination(airport);
      setSearchParams({ ...searchParams, destination: airport.iataCode });
      setShowDestinationSuggestions(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setError(null);
    setIsSearching(true);
    if (onLoading) onLoading(true);

    const originCode = selectedOrigin?.iataCode?.toUpperCase();
    const destinationCode = selectedDestination?.iataCode?.toUpperCase();

    if (!originCode || !destinationCode) {
      setError('Veuillez sélectionner un aéroport de départ et d’arrivée valide.');
      setIsSearching(false);
      return;
    }

    try {
      const payload = {
        origin: originCode,
        destination: destinationCode,
        departureDate: searchParams.departureDate,
        returnDate: tripType === 'roundtrip' ? searchParams.returnDate : undefined,
        adults: searchParams.adults,
        children: searchParams.children,
        infants: searchParams.infants,
        travelClass: searchParams.travelClass,
        nonStop: searchParams.nonStop,
        currencyCode: currency.code
      };

      console.log('🔍 Recherche vols avec payload:', payload);
      const results = await flightService.searchFlights(payload);
      console.log('✅ Résultats reçus:', results);

      if (onSearchResults) onSearchResults(results);
    } catch (err) {
      console.error('Erreur recherche vols:', err);
      setError(err.response?.data?.message || 'Erreur lors de la recherche de vols.');
    } finally {
      setIsSearching(false);
      if (onLoading) onLoading(false);
    }
  };

  return (
    <form onSubmit={handleSearch} className="space-y-6">
      {/* Origine */}
      <div className="relative">
        <input
          type="text"
          placeholder="Départ"
          value={searchParams.origin}
          onChange={(e) => {
            setSearchParams({ ...searchParams, origin: e.target.value });
            searchAirports(e.target.value, 'origin');
          }}
          onFocus={() => searchParams.origin && searchAirports(searchParams.origin, 'origin')}
          onBlur={() => setTimeout(() => setShowOriginSuggestions(false), 200)}
          className="input"
        />
        {showOriginSuggestions && originSuggestions.length > 0 && (
          <div className="absolute z-50 w-full bg-white border rounded shadow max-h-60 overflow-auto">
            {originSuggestions.map((airport) => (
              <div key={airport.iataCode} onClick={() => selectAirport(airport, 'origin')} className="p-2 cursor-pointer hover:bg-gray-100">
                {airport.name} ({airport.iataCode})
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Destination */}
      <div className="relative">
        <input
          type="text"
          placeholder="Arrivée"
          value={searchParams.destination}
          onChange={(e) => {
            setSearchParams({ ...searchParams, destination: e.target.value });
            searchAirports(e.target.value, 'destination');
          }}
          onFocus={() => searchParams.destination && searchAirports(searchParams.destination, 'destination')}
          onBlur={() => setTimeout(() => setShowDestinationSuggestions(false), 200)}
          className="input"
        />
        {showDestinationSuggestions && destinationSuggestions.length > 0 && (
          <div className="absolute z-50 w-full bg-white border rounded shadow max-h-60 overflow-auto">
            {destinationSuggestions.map((airport) => (
              <div key={airport.iataCode} onClick={() => selectAirport(airport, 'destination')} className="p-2 cursor-pointer hover:bg-gray-100">
                {airport.name} ({airport.iataCode})
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Dates */}
      <input
        type="date"
        value={searchParams.departureDate}
        onChange={(e) => setSearchParams({ ...searchParams, departureDate: e.target.value })}
        className="input"
        required
      />
      {tripType === 'roundtrip' && (
        <input
          type="date"
          value={searchParams.returnDate}
          onChange={(e) => setSearchParams({ ...searchParams, returnDate: e.target.value })}
          className="input"
          required
        />
      )}

      {/* Bouton */}
      <button type="submit" disabled={isSearching} className="btn btn-primary">
        {isSearching ? 'Recherche en cours...' : 'Rechercher'}
      </button>

      {error && <div className="text-red-600">{error}</div>}
    </form>
  );
};

export default FlightSearch;
