import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCurrency } from '../contexts/CurrencyContext';
import { eventService, packageService } from '../services/api';

const HomeModern = () => {
  const { formatPrice } = useCurrency();
  const [searchData, setSearchData] = useState({
    from: '',
    to: '',
    date: ''
  });

  // Ã‰tats pour les donnÃ©es de l'API
  const [events, setEvents] = useState([]);
  const [packages, setPackages] = useState([]);
  const [loading, setLoading] = useState(true);

  // Ã‰tat pour le carrousel des services
  const [currentServiceIndex, setCurrentServiceIndex] = useState(0);

  // Services disponibles
  const services = [
    {
      title: "Vols Internationaux",
      image: "https://images.unsplash.com/photo-1436491865332-7a61a109cc05?w=600&h=400&fit=crop",
      tag: "Vols",
      rating: "4.9",
      link: "/flights"
    },
    {
      title: "Ã‰vÃ©nements Sportifs",
      image: "https://images.unsplash.com/photo-1461896836934-ffe607ba8211?w=600&h=400&fit=crop",
      tag: "Sport",
      rating: "4.9",
      link: "/events"
    },
    {
      title: "Concerts & Culture",
      image: "https://images.unsplash.com/photo-1470229722913-7c0e2dbbafd3?w=600&h=400&fit=crop",
      tag: "Culture",
      rating: "4.9",
      link: "/events"
    },
    {
      title: "Packages VIP",
      image: "https://images.unsplash.com/photo-1540962351504-03099e0a754b?w=600&h=400&fit=crop",
      tag: "VIP",
      rating: "5.0",
      link: "/packages"
    },
    {
      title: "HÃ©licoptÃ¨re & Jet PrivÃ©",
      image: "https://images.unsplash.com/photo-1589519160732-57fc498494f8?w=600&h=400&fit=crop",
      tag: "Luxe",
      rating: "5.0",
      link: "/packages"
    },
    {
      title: "Voyages OrganisÃ©s",
      image: "https://images.unsplash.com/photo-1488646953014-85cb44e25828?w=600&h=400&fit=crop",
      tag: "Tours",
      rating: "4.8",
      link: "/packages"
    }
  ];

  // Fonctions de navigation du carrousel
  const nextService = () => {
    setCurrentServiceIndex((prev) => (prev + 3 >= services.length ? 0 : prev + 3));
  };

  const prevService = () => {
    setCurrentServiceIndex((prev) => (prev - 3 < 0 ? Math.max(0, services.length - 3) : prev - 3));
  };

  // Ã‰tat pour la section rÃ©servation
  const [bookingDemo, setBookingDemo] = useState({
    checkIn: '10.07.2025',
    checkOut: '12.08.2025',
    guests: 3,
    destination: 'Paris, France'
  });

  // Charger les donnÃ©es au montage du composant
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        // RÃ©cupÃ©rer les donnÃ©es de l'API (6 Ã©lÃ©ments pour la page d'accueil)
        const [eventsRes, packagesRes] = await Promise.allSettled([
          eventService.getEvents({ per_page: 6 }),
          packageService.getAllPackages(1, 6)
        ]);

        // Traiter les Ã©vÃ©nements
        if (eventsRes.status === 'fulfilled' && eventsRes.value?.data) {
          const eventsData = eventsRes.value.data.data || eventsRes.value.data;
          setEvents(Array.isArray(eventsData) ? eventsData : []);
        } else {
          console.error('Erreur lors du chargement des Ã©vÃ©nements:', eventsRes.reason);
          setEvents([]);
        }

        // Traiter les packages
        if (packagesRes.status === 'fulfilled' && packagesRes.value?.data) {
          const packagesData = packagesRes.value.data.data || packagesRes.value.data;
          setPackages(Array.isArray(packagesData) ? packagesData : []);
        } else {
          console.error('Erreur lors du chargement des packages:', packagesRes.reason);
          setPackages([]);
        }
      } catch (error) {
        console.error('Erreur lors du chargement des donnÃ©es:', error);
        setEvents([]);
        setPackages([]);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchData.from && searchData.to && searchData.date) {
      // Rediriger vers la page de vols avec les paramÃ¨tres de recherche
      window.location.href = `/flights?from=${searchData.from}&to=${searchData.to}&date=${searchData.date}`;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Hero Section - Full Screen with Background Image */}
      <section className="relative h-screen flex items-center justify-center overflow-hidden">
        {/* Background Image with Overlay */}
        <div 
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage: `url('https://images.unsplash.com/photo-1436491865332-7a61a109cc05?w=1920&h=1080&fit=crop')`,
          }}
        >
          <div className="absolute inset-0 bg-gradient-to-b from-black/40 via-black/30 to-black/50"></div>
        </div>

        {/* Content */}
        <div className="relative z-10 container-custom px-4">
          <div className="max-w-3xl">
            <h1 className="text-5xl md:text-7xl font-black text-white mb-6 leading-tight">
              Votre Conciergerie<br />
              de Voyage Premium<br />
              International
            </h1>

            <div className="flex flex-wrap gap-4 mb-8">
              <Link 
                to="/flights"
                className="px-8 py-4 bg-white text-gray-900 font-bold rounded-full hover:bg-gray-100 transition-all duration-300 flex items-center space-x-2 shadow-xl"
              >
                <span>COMMENCER</span>
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </Link>

              <Link 
                to="/events"
                className="px-8 py-4 bg-white/10 backdrop-blur-md text-white font-bold rounded-full border-2 border-white/30 hover:bg-white/20 transition-all duration-300"
              >
                Ã‰vÃ©nements
              </Link>

              <Link 
                to="/packages"
                className="px-8 py-4 bg-white/10 backdrop-blur-md text-white font-bold rounded-full border-2 border-white/30 hover:bg-white/20 transition-all duration-300"
              >
                Packages VIP
              </Link>
            </div>

            {/* Search Form Fonctionnel */}
            <form onSubmit={handleSearch} className="inline-block bg-white/10 backdrop-blur-md rounded-3xl p-6 border border-white/20 w-full max-w-4xl">
              <div className="grid md:grid-cols-4 gap-4">
                <div className="md:col-span-1">
                  <label className="block text-white text-sm font-semibold mb-2">DÃ©part</label>
                  <input 
                    type="text" 
                    placeholder="Abidjan"
                    value={searchData.from}
                    onChange={(e) => setSearchData({...searchData, from: e.target.value})}
                    className="w-full px-4 py-3 rounded-xl bg-white/20 border border-white/30 text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-purple-500"
                    required
                  />
                </div>
                <div className="md:col-span-1">
                  <label className="block text-white text-sm font-semibold mb-2">Destination</label>
                  <input 
                    type="text" 
                    placeholder="Paris"
                    value={searchData.to}
                    onChange={(e) => setSearchData({...searchData, to: e.target.value})}
                    className="w-full px-4 py-3 rounded-xl bg-white/20 border border-white/30 text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-purple-500"
                    required
                  />
                </div>
                <div className="md:col-span-1">
                  <label className="block text-white text-sm font-semibold mb-2">Date</label>
                  <input 
                    type="date" 
                    value={searchData.date}
                    onChange={(e) => setSearchData({...searchData, date: e.target.value})}
                    className="w-full px-4 py-3 rounded-xl bg-white/20 border border-white/30 text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
                    required
                  />
                </div>
                <div className="md:col-span-1 flex items-end">
                  <button 
                    type="submit"
                    className="w-full px-6 py-3 bg-gradient-to-r from-purple-600 to-purple-700 text-white font-bold rounded-xl hover:from-purple-700 hover:to-purple-800 transition-all duration-300 flex items-center justify-center space-x-2 shadow-xl"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                    </svg>
                    <span>Rechercher</span>
                  </button>
                </div>
              </div>
            </form>
          </div>

          {/* Tags */}

        </div>

        {/* Scroll Down Indicator */}
        <button className="absolute bottom-8 left-1/2 transform -translate-x-1/2 w-12 h-12 bg-white rounded-full flex items-center justify-center shadow-xl hover:scale-110 transition-transform">
          <svg className="w-6 h-6 text-gray-900" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
          </svg>
        </button>
      </section>

      {/* Recherche de Vols Section - Amadeus API */}
      <section className="py-20 bg-gradient-to-br from-purple-50 via-white to-amber-50 dark:from-gray-900 dark:via-gray-800 dark:to-gray-900 relative overflow-hidden">
        {/* Decorative Elements */}
        <div className="absolute top-0 right-0 w-96 h-96 bg-purple-200 dark:bg-purple-900 rounded-full filter blur-3xl opacity-20 animate-pulse"></div>
        <div className="absolute bottom-0 left-0 w-96 h-96 bg-amber-200 dark:bg-amber-900 rounded-full filter blur-3xl opacity-20 animate-pulse" style={{animationDelay: '1s'}}></div>

        <div className="container-custom relative z-10">
          <div className="text-center mb-16">
            <div className="inline-block px-6 py-2 bg-gradient-to-r from-purple-600 to-amber-600 text-white rounded-full text-sm font-bold mb-4 shadow-lg">
              âœˆï¸ RECHERCHE DE VOLS EN TEMPS RÃ‰EL
            </div>
            <h2 className="text-5xl md:text-6xl font-black text-gray-900 dark:text-white mb-6 leading-tight">
              Trouvez Votre Vol IdÃ©al<br />
              <span className="bg-gradient-to-r from-purple-600 to-amber-600 bg-clip-text text-transparent">Avec CarrÃ© Premium</span>
            </h2>
            <p className="text-xl text-gray-600 dark:text-gray-400 max-w-2xl mx-auto mb-8">
              Recherche en temps rÃ©el parmi des milliers de vols - Prix actualisÃ©s - RÃ©servation instantanÃ©e
            </p>
          </div>

          {/* CTA Card pour la recherche de vols */}
          <div className="max-w-4xl mx-auto">
            <Link 
              to="/flights"
              className="block group relative overflow-hidden rounded-3xl shadow-2xl hover:shadow-purple-500/50 transition-all duration-700 hover:-translate-y-4"
            >
              {/* Image de fond */}
              <div className="aspect-[21/9] overflow-hidden relative">
                <img 
                  src="https://images.unsplash.com/photo-1436491865332-7a61a109cc05?w=1200&h=600&fit=crop"
                  alt="Recherche de vols"
                  className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-1000"
                />
                <div className="absolute inset-0 bg-gradient-to-r from-purple-900/90 via-purple-800/80 to-amber-900/90"></div>

                {/* Contenu */}
                <div className="absolute inset-0 flex flex-col items-center justify-center text-white p-12">
                  <div className="w-24 h-24 bg-white/20 backdrop-blur-md rounded-full flex items-center justify-center mb-6 group-hover:scale-110 transition-transform">
                    <i className="fas fa-search text-5xl"></i>
                  </div>
                  <h3 className="text-4xl md:text-5xl font-black mb-4 text-center">
                    Rechercher un Vol
                  </h3>
                  <p className="text-xl text-white/90 mb-6 text-center max-w-2xl">
                    AccÃ©dez Ã  des milliers de vols en temps rÃ©el via notre partenariat avec CarrÃ© premium
                  </p>

                  {/* Features */}
                  <div className="flex flex-wrap justify-center gap-4 mb-8">
                    <div className="bg-white/10 backdrop-blur-md px-6 py-3 rounded-full border border-white/20">
                      <i className="fas fa-bolt text-yellow-400 mr-2"></i>
                      <span className="font-semibold">RÃ©sultats instantanÃ©s</span>
                    </div>
                    <div className="bg-white/10 backdrop-blur-md px-6 py-3 rounded-full border border-white/20">
                      <i className="fas fa-globe text-blue-400 mr-2"></i>
                      <span className="font-semibold">Couverture mondiale</span>
                    </div>
                    <div className="bg-white/10 backdrop-blur-md px-6 py-3 rounded-full border border-white/20">
                      <i className="fas fa-tag text-green-400 mr-2"></i>
                      <span className="font-semibold">Meilleurs prix</span>
                    </div>
                  </div>

                  <div className="inline-flex items-center px-10 py-5 bg-white text-purple-900 font-black text-xl rounded-full group-hover:scale-110 transition-transform shadow-2xl space-x-3">
                    <span>COMMENCER LA RECHERCHE</span>
                    <i className="fas fa-arrow-right group-hover:translate-x-2 transition-transform"></i>
                  </div>
                </div>
              </div>
            </Link>
          </div>

          {/* Destinations populaires - Liens rapides */}
          <div className="mt-12 grid grid-cols-2 md:grid-cols-4 gap-6">
            {[
              { city: 'Paris', code: 'CDG', flag: 'ðŸ‡«ðŸ‡·', price: '450,000' },
              { city: 'Dubai', code: 'DXB', flag: 'ðŸ‡¦ðŸ‡ª', price: '550,000' },
              { city: 'New York', code: 'JFK', flag: 'ðŸ‡ºðŸ‡¸', price: '750,000' },
              { city: 'Londres', code: 'LHR', flag: 'ðŸ‡¬ðŸ‡§', price: '420,000' }
            ].map((dest, idx) => (
              <Link
                key={idx}
                to="/flights"
                className="bg-white dark:bg-gray-800 rounded-2xl p-6 shadow-lg hover:shadow-2xl transition-all duration-300 hover:-translate-y-2 text-center group"
              >
                <div className="text-4xl mb-3">{dest.flag}</div>
