import React, { useState } from 'react';
import HeaderModern from '../components/layout/HeaderModern';
import FooterModern from '../components/layout/FooterModern';
import FlightSearch from '../components/flights/FlightSearch';
import FlightResults from '../components/flights/FlightResults';
import { useLanguage } from '../contexts/LanguageContext';

const FlightsModern = () => {
  const { t } = useLanguage();
  const [searchResults, setSearchResults] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleSearchResults = (results) => {
    setSearchResults(results);
    // Scroll vers les résultats
    setTimeout(() => {
      document.getElementById('results-section')?.scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }, 100);
  };

  const handleLoading = (loading) => {
    setIsLoading(loading);
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <HeaderModern />

      {/* Hero Section */}
      <section className="relative h-[60vh] flex items-center justify-center overflow-hidden">
        <div 
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage: `url('https://images.unsplash.com/photo-1436491865332-7a61a109cc05?w=1920&h=1080&fit=crop')`,
          }}
        >
          <div className="absolute inset-0 bg-gradient-to-b from-black/60 via-black/40 to-black/60"></div>
        </div>

        <div className="relative z-10 container mx-auto px-4 text-center">
          <h1 className="text-5xl md:text-7xl font-black text-white mb-6 leading-tight">
            {t('findYourIdealFlight', 'Trouvez Votre')}<br />
            <span className="bg-gradient-to-r from-purple-400 to-amber-400 bg-clip-text text-transparent">
              {t('idealFlight', 'Vol Idéal')}
            </span>
          </h1>
          <p className="text-xl md:text-2xl text-white/90 max-w-3xl mx-auto">
            {t('realTimeFlightSearch', 'Recherche en temps réel via Amadeus - Des milliers de vols disponibles')}
          </p>

          {/* Badges */}
          <div className="flex flex-wrap justify-center gap-4 mt-8">
            <div className="bg-white/10 backdrop-blur-md px-6 py-3 rounded-full border border-white/20">
              <i className="fas fa-check-circle text-green-400 mr-2"></i>
              <span className="text-white font-semibold">{t('realTimePrices', 'Prix en temps réel')}</span>
            </div>
            <div className="bg-white/10 backdrop-blur-md px-6 py-3 rounded-full border border-white/20">
              <i className="fas fa-shield-alt text-blue-400 mr-2"></i>
              <span className="text-white font-semibold">{t('secureBooking', 'Réservation sécurisée')}</span>
            </div>
            <div className="bg-white/10 backdrop-blur-md px-6 py-3 rounded-full border border-white/20">
              <i className="fas fa-headset text-purple-400 mr-2"></i>
              <span className="text-white font-semibold">{t('support24_7', 'Support 24/7')}</span>
            </div>
          </div>
        </div>
      </section>

      {/* Search Section */}
      <section className="relative z-20 -mt-32">
        <div className="container mx-auto px-4">
          <FlightSearch 
            onSearchResults={handleSearchResults}
            onLoading={handleLoading}
          />
        </div>
      </section>

      {/* Results Section */}
      {(searchResults || isLoading) && (
        <section id="results-section" className="py-16">
          <div className="container mx-auto px-4">
            <FlightResults 
              results={searchResults}
              loading={isLoading}
            />
          </div>
        </section>
      )}

      {/* Info Section - Affiché uniquement si pas de recherche */}
      {!searchResults && !isLoading && (
        <section className="py-20 bg-white dark:bg-gray-800">
          <div className="container mx-auto px-4">
            <div className="text-center mb-12">
              <h2 className="text-4xl font-bold text-gray-800 dark:text-white mb-4">
                {t('whyChooseUs', 'Pourquoi Choisir Carré Premium ?')}
              </h2>
              <p className="text-xl text-gray-600 dark:text-gray-400">
                {t('bestFlightExperience', 'La meilleure expérience de réservation de vols')}
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
              {/* Avantage 1 */}
              <div className="bg-gradient-to-br from-purple-50 to-purple-100 dark:from-purple-900/20 dark:to-purple-800/20 rounded-2xl p-8 text-center">
                <div className="w-20 h-20 bg-purple-600 rounded-full flex items-center justify-center mx-auto mb-6">
                  <i className="fas fa-globe text-3xl text-white"></i>
                </div>
                <h3 className="text-2xl font-bold text-gray-800 dark:text-white mb-4">
                  {t('globalCoverage', 'Couverture Mondiale')}
                </h3>
                <p className="text-gray-600 dark:text-gray-400">
                  {t('globalCoverageDesc', 'Accédez à des milliers de vols dans le monde entier grâce à notre partenariat avec Amadeus')}
                </p>
              </div>

              {/* Avantage 2 */}
              <div className="bg-gradient-to-br from-amber-50 to-amber-100 dark:from-amber-900/20 dark:to-amber-800/20 rounded-2xl p-8 text-center">
                <div className="w-20 h-20 bg-amber-600 rounded-full flex items-center justify-center mx-auto mb-6">
                  <i className="fas fa-bolt text-3xl text-white"></i>
                </div>
                <h3 className="text-2xl font-bold text-gray-800 dark:text-white mb-4">
                  {t('instantBooking', 'Réservation Instantanée')}
                </h3>
                <p className="text-gray-600 dark:text-gray-400">
                  {t('instantBookingDesc', 'Confirmation immédiate avec PNR et billets électroniques envoyés par email')}
                </p>
              </div>

              {/* Avantage 3 */}
              <div className="bg-gradient-to-br from-green-50 to-green-100 dark:from-green-900/20 dark:to-green-800/20 rounded-2xl p-8 text-center">
                <div className="w-20 h-20 bg-green-600 rounded-full flex items-center justify-center mx-auto mb-6">
                  <i className="fas fa-shield-check text-3xl text-white"></i>
                </div>
                <h3 className="text-2xl font-bold text-gray-800 dark:text-white mb-4">
                  {t('securePayment', 'Paiement Sécurisé')}
                </h3>
                <p className="text-gray-600 dark:text-gray-400">
                  {t('securePaymentDesc', 'Transactions 100% sécurisées avec plusieurs options de paiement disponibles')}
                </p>
              </div>
            </div>
          </div>
        </section>
      )}

      {/* Popular Destinations - Affiché uniquement si pas de recherche */}
      {!searchResults && !isLoading && (
        <section className="py-20 bg-gray-50 dark:bg-gray-900">
          <div className="container mx-auto px-4">
            <div className="text-center mb-12">
              <h2 className="text-4xl font-bold text-gray-800 dark:text-white mb-4">
                {t('popularDestinations', 'Destinations Populaires')}
              </h2>
              <p className="text-xl text-gray-600 dark:text-gray-400">
                {t('discoverTopDestinations', 'Découvrez nos destinations les plus prisées')}
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
              {[
                { city: 'Paris', country: 'France', code: 'CDG', image: 'https://images.unsplash.com/photo-1502602898657-3e91760cbb34?w=800&h=600&fit=crop', price: 450000 },
                { city: 'Dubai', country: 'UAE', code: 'DXB', image: 'https://images.unsplash.com/photo-1512453979798-5ea266f8880c?w=800&h=600&fit=crop', price: 550000 },
                { city: 'New York', country: 'USA', code: 'JFK', image: 'https://images.unsplash.com/photo-1496442226666-8d4d0e62e6e9?w=800&h=600&fit=crop', price: 750000 }
              ].map((dest, index) => (
                <div key={index} className="group relative overflow-hidden rounded-2xl shadow-xl hover:shadow-2xl transition-all duration-500 cursor-pointer">
                  <div className="relative h-80">
                    <img 
                      src={dest.image} 
                      alt={dest.city}
                      className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-700"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent"></div>

                    <div className="absolute bottom-0 left-0 right-0 p-6 text-white">
                      <h3 className="text-3xl font-bold mb-2">{dest.city}</h3>
                      <p className="text-white/80 mb-4">{dest.country}</p>
                      <div className="flex justify-between items-center">
                        <span className="text-2xl font-bold">{t('from', 'À partir de')} {dest.price.toLocaleString()} XOF</span>
                        <button className="bg-purple-600 hover:bg-purple-700 px-6 py-2 rounded-lg transition-colors">
                          {t('search', 'Rechercher')}
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>
      )}

      <FooterModern />
    </div>
  );
};

export default FlightsModern;
